package com.denysnovoa.trainingkotlinimport android.os.Bundleimport android.support.v7.app.AppCompatActivityimport android.widget.Buttonimport android.widget.EditTextimport android.widget.TextViewimport android.widget.Toastclass MainActivity : AppCompatActivity() {  override fun onCreate(savedInstanceState: Bundle?) {    super.onCreate(savedInstanceState)    setContentView(R.layout.activity_main)    val text = findViewById(R.id.textView) as TextView    text.text = "Esto es un test"    val button = findViewById(R.id.button) as Button    val message = findViewById(R.id.message) as EditText    button.setOnClickListener { toast("mensage: ${message.text}") }  }  private fun toast(message: String) {    Toast.makeText(this, message, Toast.LENGTH_SHORT).show()  }}